    def save_ticket_price(self, row, col):
        """L∆∞u gi√° v√© v√†o file 'giave.txt' khi ch·ªânh s·ª≠a √¥ trong b·∫£ng."""
        if col != 0:  # Ch·ªâ l∆∞u khi ch·ªânh s·ª≠a c·ªôt "Gi√° v√©"
            return

        item = self.tableWidget_2.item(row, col)
        if item is None:
            return

        new_price = item.text().strip()  # L·∫•y gi√° tr·ªã m·ªõi nh·∫≠p v√†o
        movie_name_item = self.tableWidget_2.verticalHeaderItem(row)

        if movie_name_item:
            movie_name = movie_name_item.text().strip()
        else:
            return

        # C·∫≠p nh·∫≠t gi√° v√© trong file giave.txt
        try:
            with open("giave.txt", "r", encoding="utf-8") as file:
                lines = file.readlines()

            with open("giave.txt", "w", encoding="utf-8") as file:
                updated = False
                for line in lines:
                    columns = line.strip().split("|")

                    # N·∫øu phim tr√πng, c·∫≠p nh·∫≠t gi√° v√©
                    if columns[0].strip() == movie_name:
                        columns[1] = new_price  # C·∫≠p nh·∫≠t c·ªôt gi√° v√©
                        file.write(" | ".join(columns) + "\n")
                        updated = True
                    else:
                        file.write(line)

                # N·∫øu phim ch∆∞a c√≥ trong file, th√™m m·ªõi
                if not updated:
                    file.write(f"{movie_name} | {new_price}\n")

            print(f"‚úÖ ƒê√£ l∆∞u gi√° v√© m·ªõi {new_price} cho phim {movie_name}")

        except Exception as e:
            print(f"üö® L·ªói khi l∆∞u gi√° v√©: {e}")

    def load_ticket_data(self):
        """ƒê·ªçc d·ªØ li·ªáu t·ª´ file datadoanhthu.txt v√† c·∫≠p nh·∫≠t b·∫£ng Qu·∫£n l√Ω v√©."""
        ticket_data = {}

        try:
            with open("datadoanhthu.txt", "r", encoding="utf-8") as file:
                for line in file:
                    columns = line.strip().split("|")

                    # Ki·ªÉm tra xem d√≤ng c√≥ √≠t nh·∫•t 2 c·ªôt kh√¥ng (T√™n phim | S·ªë v√© ƒë√£ b√°n)
                    if len(columns) < 2:
                        continue

                    # Chu·∫©n h√≥a t√™n phim (x√≥a kho·∫£ng tr·∫Øng th·ª´a v√† chuy·ªÉn v·ªÅ ch·ªØ th∆∞·ªùng)
                    movie_name = columns[0].strip().lower()

                    try:
                        tickets_sold = int(columns[1].strip())  # Chuy·ªÉn s·ªë v√© ƒë√£ b√°n th√†nh s·ªë nguy√™n
                    except ValueError:
                        print(f"L·ªói chuy·ªÉn ƒë·ªïi s·ªë v√© cho phim '{movie_name}', b·ªè qua d√≤ng n√†y!")
                        continue

                    # C·ªông d·ªìn s·ªë v√© ƒë√£ b√°n n·∫øu phim ƒë√£ c√≥ trong dict
                    if movie_name in ticket_data:
                        ticket_data[movie_name] += tickets_sold
                    else:
                        ticket_data[movie_name] = tickets_sold

        except FileNotFoundError:
            print("üö® L·ªói: File 'datadoanhthu.txt' kh√¥ng t·ªìn t·∫°i!")
            return
        except Exception as e:
            print(f"üö® L·ªói khi ƒë·ªçc d·ªØ li·ªáu: {e}")
            return

        # C·∫≠p nh·∫≠t d·ªØ li·ªáu l√™n b·∫£ng
        total_tickets = 216  # Gi·∫£ s·ª≠ t·ªïng s·ªë v√© l√† 216
        row_count = self.tableWidget_2.rowCount()

        for row in range(row_count):
            item = self.tableWidget_2.verticalHeaderItem(row)  # L·∫•y t√™n phim t·ª´ ti√™u ƒë·ªÅ h√†ng
            if item:
                movie_name_in_table = item.text().strip().lower()  # Chuy·ªÉn v·ªÅ ch·ªØ th∆∞·ªùng ƒë·ªÉ so kh·ªõp
                tickets_sold = ticket_data.get(movie_name_in_table, 0)  # L·∫•y s·ªë v√© ƒë√£ b√°n
                tickets_remaining = total_tickets - tickets_sold  # T√≠nh s·ªë v√© c√≤n l·∫°i

                # C·∫≠p nh·∫≠t b·∫£ng
                self.tableWidget_2.setItem(row, 1, QtWidgets.QTableWidgetItem(str(tickets_sold)))
                self.tableWidget_2.setItem(row, 2, QtWidgets.QTableWidgetItem(str(tickets_remaining)))

    def refresh_ticket_data(self):
        """L√†m m·ªõi d·ªØ li·ªáu khi nh·∫•n n√∫t 'L√†m m·ªõi'."""
        self.tableWidget_2.clearContents()  # X√≥a d·ªØ li·ªáu c≈©
        self.load_ticket_data()  # T·∫£i l·∫°i t·ª´ file

    def load_movie_data(self):
        """ ƒê·ªçc d·ªØ li·ªáu t·ª´ datatime.txt v√† c·∫≠p nh·∫≠t v√†o tableWidget, kh√≥a c·ªôt 1 v√† 2 """
        movie_data = {}

        try:
            with open("datatime.txt", "r", encoding="utf-8") as file:
                lines = file.readlines()

            # B·ªè d√≤ng ti√™u ƒë·ªÅ (d√≤ng ƒë·∫ßu ti√™n)
            for line in lines[1:]:
                columns = line.strip().split("|")
                if len(columns) < 3:
                    continue  # B·ªè qua n·∫øu d√≤ng kh√¥ng ƒë·ªß d·ªØ li·ªáu

                movie_name = columns[0].strip().lower()  # Chu·∫©n h√≥a t√™n phim
                genre = columns[1].strip()  # Th·ªÉ lo·∫°i
                duration = columns[2].strip()  # Th·ªùi l∆∞·ª£ng

                movie_data[movie_name] = (genre, duration)  # L∆∞u v√†o dictionary

        except FileNotFoundError:
            print("‚ö† File 'datatime.txt' kh√¥ng t·ªìn t·∫°i!")
            return
        except Exception as e:
            print(f"üö® L·ªói khi ƒë·ªçc file 'datatime.txt': {e}")
            return

        # C·∫≠p nh·∫≠t d·ªØ li·ªáu v√†o tableWidget
        for row in range(self.tableWidget.rowCount()):
            item = self.tableWidget.verticalHeaderItem(row)
            if item:
                movie_name_in_table = item.text().strip().lower()
                if movie_name_in_table in movie_data:
                    genre, duration = movie_data[movie_name_in_table]

                    # C·∫≠p nh·∫≠t c·ªôt "Th·ªÉ lo·∫°i" (c·ªôt 1)
                    self.tableWidget.setItem(row, 0, QtWidgets.QTableWidgetItem(genre))
                    self.tableWidget.item(row, 0).setFlags(QtCore.Qt.ItemFlag.ItemIsEnabled)  # Kh√≥a c·ªôt 1

                    # C·∫≠p nh·∫≠t c·ªôt "Th·ªùi l∆∞·ª£ng" (c·ªôt 2)
                    self.tableWidget.setItem(row, 1, QtWidgets.QTableWidgetItem(duration))
                    self.tableWidget.item(row, 1).setFlags(QtCore.Qt.ItemFlag.ItemIsEnabled)  # Kh√≥a c·ªôt 2

        print("‚úÖ D·ªØ li·ªáu phim ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t t·ª´ datatime.txt.")

